import Layout from '../components/Layout';
import React from 'react'
import fetch from 'isomorphic-unfetch';


export default function Details (props) {
    const {skills} = props;
    let skill_level;
    switch (skills.skill_level) {
        case 1:
            skill_level = 1;
            break;
        case 2:
            skill_level = 2;
            break;
        case 3:
            skill_level = 3;
            break;
        case 4:
            skill_level = 4;
            break;
        case 5:
            skill_level = 5;
            break;
        case 6:
            skill_level = 6;
            break;
    }
    return (
        <Layout>
            <h1>{skills.description}</h1>
            <h2>Skill level: {skills.skill_level}</h2>
        </Layout>
    )
}


Details.getInitialProps = async ({query}) => {
    let res, data, skills;
    try {
        res = await fetch(`http://127.0.0.1:8000/api/v1/skill/`)
        data = await res.json();
        skills = data.objects.find((skills) => skills.id === parseInt(query.id));
    } catch(error){
        console.error(error);
    }
    return {
        skills
    }
};

// export default Details;
